    state = {
        value:1
    }

    addAge = () => {
        const updatState = this.state.value +=1;

        this.setState({
            updatState
        })
    }
    minsAge = () => {
        const updatState = this.state.value -= 1;

        this.setState({
            updatState
        })
    }


    render() {
        const year = new Date().getFullYear();
        return ( 
        <div className = "MayCars" >
            <h1 > { this.props.titre } </h1>  
            <button onClick = { this.minsAge } > Age -1 </button>
