    //     if (e.target.classList.contains('styled')) {
    //         e.target.classList.remove('styled');
    //     } else {
    //         e.target.classList.add('styled');
    //     }
    // }

    state = {
        voitures: [
            { name: "Ford", color: "red", annee: "2029" },
            { name: "Mercedes", color: "black", annee: "2020" },
            { name: "Audi", color: "green", annee: "2011" }
        ]
    }

    addAge = () => {
        const updatState = this.state.voitures.map((param) => {
            return param.annee -= 10;
        })

        this.setState({
            updatState
        })
    }
    render() {
        const year = new Date().getFullYear();
        return ( <
            div className = "MayCars" >
            <
            h1 > { this.props.titre } < /h1>  <
            button onClick = { this.addAge } > Age - 10 < /button><
            Car year = { this.state.voitures[0].annee }
            color = { this.state.voitures[0].color } > { this.state.voitures[0].name } < /Car> <
            Car year = { this.state.voitures[1].annee }
            color = { this.state.voitures[1].color } > { this.state.voitures[1].name } < /Car> <
            Car year = { this.state.voitures[2].annee }
            color = { this.state.voitures[2].color } > { this.state.voitures[2].name } < /Car> < /
            div > );
    }
}


export default MyCars;
