import React from 'react';
import MyCars from './Components/MyCars';
import './App.css';

class App extends React.Component {


    changeTitre = () => {
        console.log("onClick changeTitre");
        const updatState = this.state.voitures.map((param) => {
            return param.annee -= 10;
        })

        this.setState({
            updatState
        })

    }

    static getDerivedStateFromProps(props, state) {
        console.log("getDerivedStateFromProps");
        console.log(props);
        console.log(state);
        return null;
    }

    shouldComponentUpdate(nextProps, nextState) {
        console.log("shouldComponentUpdate");
        return true;
    }


    render() {
        console.log("je suis dans le render");
        return ( 
            < div className = "App" >
                <bouton onClick={this.changeTitre}>changeTitre</bouton>
                <MyCars titre = { this.state.titre }/> 
            </div >
        );
    }

}



export default App;
